import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import clevertap from 'clevertap-web-sdk';

function App() {
  const eventPush = () => (event) => {
    console.log('CT event raised');
    const event_name = document.getElementById('event_name').value
    const values = [...inputs];
    if(values.length == 0){
    clevertap.event.push(event_name);
    }else{
      clevertap.event.push(event_name,JSON.stringify(values));
    }
    console.log('CT event Pushed');
  }

  const onUserLogin = () => (event) => {
    clevertap.onUserLogin.push({
      "Site": {
        "Name": "TestProfile ReactSDK",            // String
        "Email": "ct_react_sdk@gmail.com",         // Email address of the user
        "Phone": "+14155551234",           // Phone (with the country code)
        "Gender": "M",                     // Can be either M or F
      }
    })
    console.log('CT profile Pushed');
  }
  const userProfileUpdate = () => (event) => {
    clevertap.profile.push({
      "Site": {
        "Gender": "F",
        "Name": "TestProfile ReactSDK1",
      }       // Can be either M or F
    })
    console.log('CT profile updated');
  }

  const [inputs, setInputs] = useState([]);

  const handleChange = (index, event, keyVal) => {
    const values = [...inputs];
    if (keyVal == 'KEY') {
      values[index].value[0] = event.target.value;
    } else {
      values[index].value[1] = event.target.value;
    }
    setInputs(values);
  };

  const handleAdd = () => {
    setInputs([...inputs, { value: ['',''] }]);
  };

  const handleRemove = (index) => {
    const values = [...inputs];
    values.splice(index, 1);
    setInputs(values);
  };
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
        <br></br>
        <button onClick={onUserLogin()}>
          User Login!
        </button>
        <button onClick={userProfileUpdate()}>
          Update Profile
        </button>
        <form>
          <div>
            <input id='event_name'
              type="text"
              placeholder='Event Name' />

            <button type="button" onClick={() => eventPush()}>
              Push Event
            </button>
          </div>
          {inputs.map((input, index) => (
            <div key={index}>
              <input
                type="text"
                value={input.value[0]}
                onChange={(event) => handleChange(index, event, 'KEY')}
              />
              <input
                type="text"
                value={input.value[1]}
                onChange={(event) => handleChange(index, event, 'VALUE')}
              />
              <button type="button" onClick={() => handleRemove(index)}>
                Remove
              </button>
            </div>
          ))}
          <button type="button" onClick={handleAdd}>
            Add Property
          </button>
        </form>
      </header>
    </div>
  );
}

export default App;
